import React from 'react';
import ReactDOM from 'react-dom';
import name,{name2,myName,add} from './App';
import 'bootstrap/dist/css/bootstrap.min.css';

var name1="arman";

// var time=new Date();
// var date=time.toLocaleTimeString();
var time=new Date(2020,8,6,5);
var getHour=time.getHours();
var message="";
const cssStyle={};
if(getHour>=12 && getHour<18)
{
  message="Good Noon!";
  cssStyle.color="yellow";
}
else if(getHour<12)
{
  message="Good Morning!";
  cssStyle.color="red";
}
else if(getHour>=18 && getHour<=21)
{
  message="Good Evening!!";
  cssStyle.backgroundColor="orange";
}
else if(getHour>=21)
{
  message="Good Night!!";
  cssStyle.backgroundColor="black";
}
else
{
  message="Input Incorrect";
}

ReactDOM.render(
  <>
  <div className="row">
    <div className="col-sm-6">{name}</div>
    <div className="col-sm-6">{name2}</div>
  </div>


   <h5>Hello, Good Morning!</h5>
   <h1 style={{cssStyle},{textAlign:'center'}}>{message}</h1>
   <h2>{myName()}</h2>
   <h5>{add(5000,499)}</h5>
  </>
   ,
  document.getElementById('root')
);
